package example.com;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import com.dothantech.lpapi.LPAPI;public class MainActivity extends AppCompatActivity {	LPAPI api = LPAPI.Factory.createInstance();	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.activity_main);		double width =20;		double height =20;		int qrcodeWidth = 16;		double margin = 0.5;		int orientation = 0;		api.startJob(width,height,orientation);		api.draw2DQRCode("",(width-qrcodeWidth)/2.0,margin,qrcodeWidth);		api.setItemHorizontalAlignment(LPAPI.ItemAlignment.CENTER);		api.drawText("",0,qrcodeWidth+margin*2,width,height-qrcodeWidth-margin*2,3.125);		api.drawText("",margin*2,qrcodeWidth+margin*2,width-margin*4,height-qrcodeWidth-margin*2,3.125);	}}